mapper.select:
select
,report.* from mokjang_report mokjangreport left join ( select
, from report report inner join report_item_hist hist on hist.report_seq = report.report_seq ) report on report_weeks = mokjangreport.weeks and report_dept_seq=mokjangreport.dept_seq
mokjangreport.dept_seq = #{deptSeq} and mokjangreport.weeks = #{weeks}
mokjangreport.mokjang_report_seq = #{mokjangReportSeq}
